require_relative "../problem/sherlock_and_valid_string"

describe "Problem sherlock_and_valid_string" do
  it { expect(isValid('aabbcd')).to eq('NO') }
  it { expect(isValid('aabbccddeefghi')).to eq('NO') }
  it { expect(isValid('abcdefghhgfedecba')).to eq('YES') }
  it { expect(isValid('')).to eq('YES') }
end
